import {useEffect, useState} from 'react';
import axios from "axios";
import './App.css';

function App() {
  const [characters, setCharacters] = useState([]);
  const [nameQuery, setNameQuery] = useState('');
  const [statusQuery, setStatusQuery] = useState('');

  // useEffect(() => {

  //   const fetchData = async () => {
  //     try {
  //       const {data} = await axios.get(`https://rickandmortyapi.com/api/character/?name=${query}`)
  //       setCharacters(data.results)
  //     } catch(error) {
  //       console.error(error)
  //     }
  //   }
  //   fetchData()
  // }, [nameQuery]);

  useEffect (() => {
    const fetchStatus = async () => {
      try {
        const {data} = await axios.get (`https://rickandmortyapi.com/api/character/?name=${nameQuery}&status=${statusQuery}`)
        setCharacters(data.results)
      } catch(error) {
        console.error(error)
      }
    }
    fetchStatus()
  }, [nameQuery, statusQuery]);





  return (
    <div className="App">
      <div className='searchbar'>
        <input 
        type='text'
        placeholder={"name"} 
        value={nameQuery}
        className={'searchInput'}
        onChange={(e) => setNameQuery(e.target.value)}/>
        <select id="status" name="status" value={statusQuery} onChange={(e) => setStatusQuery(e.target.value)}>
        <option value="alive">Жив</option>
        <option value="dead">Мертв</option>
        </select>
        <select id="fruits" name="Раса">
      <option value="human">Человек</option>
      <option value="alien">Инопланетянин</option>
      </select>

      </div>
      <div className='results'>
        {characters.map(character => (
          <div>
            <img src={character.image} alt={character.name}></img>
            {character.name}
          </div>
        ))}
      </div>
    </div>
  );
}

export default App;
