import {useEffect, useState} from 'react';
import axios from "axios";
import './App.css';

function App() {
  const [characters, setCharacters] = useState([]);
  const [nameQuery, setNameQuery] = useState('');
  const [statusQuery, setStatusQuery] = useState('');
  const [speciesQuery, setSpeciesQuery] = useState('');
  const [episodeQuery, setEpisodeQuery] = useState('');



  useEffect(() => {
    const fetchEpisode = async () => {
      try {
      const {data} = await axios.get(`https://rickandmortyapi.com/api/episode/${episodeQuery}`)
      console.log(data.characters);
      // const episodeCharacters = data.characters;
      const id = data.characters.map((item) => {
      const charactersId = item.slice(42);
        return charactersId;
       });
       console.log(id);


       function sliceIntoChunks(arr, chunkSize) {
        const res = [];
        for (let i = 0; i < arr.length; i += chunkSize) {
            const chunk = arr.slice(i, i + chunkSize);
            res.push(chunk);
        }
        return res;
    }
      const tryId = sliceIntoChunks(id, 1);
      console.log(tryId);

      tryId.forEach((id) => {
        const {charactersID} = axios.get(`https://rickandmortyapi.com/api/character/${id}`)
        console.log(charactersID);
      })


      // const {dataCharacters} = await axios.get(`https://rickandmortyapi.com/api/character/${tryId}`)
      // console.log(dataCharacters);
      // setCharacters(dataCharacters.results);
      } catch(error) {
        console.error(error)
      }
    }
    fetchEpisode()
  }, [episodeQuery]);

  // useEffect (() => {
  //   const fetchStatus = async () => {
  //     try {
  //       const {data} = await axios.get (`https://rickandmortyapi.com/api/character/?name=${nameQuery}&status=${statusQuery}&species=${speciesQuery}`)
  //       setCharacters(data.results)
  //     } catch(error) {
  //       console.error(error)
  //     }
  //   }
  //   fetchStatus()
  // }, [nameQuery, statusQuery, speciesQuery]);

  // useEffect (() => {
  //   const fetchEpisode = async () => {
  //     try {
  //       const [episodeData] = await axios.get (`https://rickandmortyapi.com/api/episode/${episodeQuery}`)
  //       console.log(episodeData)
  //     } catch(error) {
  //       console.error(error)
  //     }
  //   }
  //   fetchEpisode()
  // }, [episodeQuery]);


  return (
    <div className="App">
      <div className='searchbar'>
        <input 
        name='name'
        type='text'
        placeholder={"name"} 
        value={nameQuery}
        className={'searchInput'}
        onChange={(e) => setNameQuery(e.target.value)}/>
        <select id="status" name="status" value={statusQuery} onChange={(e) => setStatusQuery(e.target.value)}>
        <option value="alive">Жив</option>
        <option value="dead">Мертв</option>
        </select>
      <select id="species" name="species" value={speciesQuery} onChange={(e) => setSpeciesQuery(e.target.value)}>
        <option value="Human">Человек</option>
        <option value="Alien">Инопланетянин</option>
      </select>
      <input 
        name='episode'
        type='text'
        placeholder={"episode"} 
        value={episodeQuery}
        className={'searchInput'}
        onChange={(e) => {
          setEpisodeQuery(e.target.value) 
          console.log(episodeQuery)}
        }/>
      </div>
      <div className='results'>
        {characters.map(character => (
          <div>
            <img src={character.image} alt={character.name}></img>
            {character.name}
            <span></span>
          </div>
        ))}
      </div>
    </div>
  );
}

export default App;
