import {useEffect, useState} from 'react';
import axios from "axios";
import './App.css';

function App() {
  const [characters, setCharacters] = useState([]);
  const [query, setQuery] = useState('');
  const handleSubmit = e => {
    e.preventDefault();
  };

    const searchCharacter = async () => {

        const result = await axios(`https://rickandmortyapi.com/api/character/?name${query}`);
        setCharacters(result.data);
        console.log(result.data)
      };
      useEffect(() => {
        searchCharacter();
      }, [query],);

  const [value, setValue] = useState('');

  const filteredCharacters = characters.filter(character => {
    return character.name.toLowerCase().includes(value.toLowerCase())
  })



  return (
    <div className="App">
      <div className='searchbar'>
        <form>
          <input type='text' placeholder={"name"} className={'nameInput'} 
          onChange={(event) => setValue(event.target.value)}
          />
            <select id="fruits" name="fruits">
            <option value="apple">Яблоко</option>
            <option value="banana">Банан</option>
            <option value="orange">Апельсин</option>
            </select>
            <select id="fruits" name="fruits">
            <option value="apple">Яблоко</option>
            <option value="banana">Банан</option>
            <option value="orange">Апельсин</option>
            </select>
            <input type='text' placeholder={"Episode"} className={'episodeInput'}/> 
        </form>
      </div>
      <div className='results'>
        {filteredCharacters.map(character => (
          <div>
            <img src={character.image} alt={character.name}></img>
            {character.name}
          </div>
        ))}
      </div>
    </div>
  );
}

export default App;
