  useEffect (() => {
    const fetchStatus = async () => {
      try {
        const {data} = await axios.get (`https://rickandmortyapi.com/api/character/?name=${nameQuery}&status=${statusQuery}&species=${speciesQuery}`)
        setCharacters(data.results)
      } catch(error) {
        alert('Такого существа нет!')
      }
    }
    fetchStatus()
  }, [nameQuery, statusQuery, speciesQuery]);



  return (
    <div className="App">
      <h1 className='title'>Вселенная Рик и Морти</h1>
      <div className='searchbar'>
        <input 
        name='name'
        type='text'
        placeholder="Name" 
        value={nameQuery}
        className='searchInput'
        onChange={(e) => setNameQuery(e.target.value)}/>
        <div className='select__container'>
          <select  className='statusInput'id="status" name="status" value={statusQuery} onChange={(e) => setStatusQuery(e.target.value)}>
        <option value="alive">Жив</option>
        <option value="dead">Мертв</option>
        </select>
      <select id="species"  className='speciesInput' name="species" value={speciesQuery} onChange={(e) => setSpeciesQuery(e.target.value)}>
        <option value="Human">Человек</option>
        <option value="Alien">Инопланетянин</option>
        </select>
        </div>

      </div>
      <div className='results__container'>
        <h2 className='results__title'>Найдено</h2>
        <div className='card__container'>
            {characters.map(character => (
          <div className='card'>
            <img className='card__image' src={character.image} alt={character.name}></img>
            <p className='card__name'><span className='span'>Name:</span> {character.name}</p>
            <p className='card__species'><span className='span'>Species:</span> {character.species}</p>
          </div>
        ))} 
        </div>

      </div>
    </div>
  );
}

export default App;
